import React, { Component } from "react"
import "./App.css"
import Table from "./components/Table"

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      numRows: 3,
      numColumns: 2,
      chosenColor: ""
    }
  }
  addRow = () => {
    let existingRows = this.state.numRows;
    this.setState({
      numRows: existingRows +1
    })
  }
  addColumn = () =>{
    let existingColumns = this.state.numColumns;
    this.setState({
      numColumns: existingColumns+1
    })
  }
  deleteRow = () =>{
    let existingRows = this.state.numRows;
    this.setState({
      numRows: existingRows-1
    })
  }
  deleteColumn = () =>{
    let existingColumns = this.state.numRows;
    this.setState({
      numRows:existingColumns-1
    })
  }
  render(){
    return (
      <div className="App">
        <h1>DOM Challenges III with ReactJS</h1>
        <div className="addButtons">
        <button id="ar" type="button" onClick={() => this.addRow()}>Add a Row</button>
        <button id="ac" type="button" onClick={() => this.addColumn()}>Add a Column</button>
        </div>
        <br></br>
        <div className="removeButtons">
        <button id="rr" type="button" onClick={()=>this.deleteRow()}>Remove a Row</button>
        <button id="rc" type="button" onClick={()=>this.deleteColumn()}>Remove a Column</button>
        </div>
        <br></br>
        <div id="colorMenu">
          <label>Select a color to shade in the cell: </label>
          <select name="colors" id="colors" onChange={(event) => {
            this.setState({
              color: event.target.value
            })
          }}>
            <option value="blue">Blue</option>
            <option value="red">Red</option>
            <option value="lime">Green</option>
            <option value="purple">Purple</option>
            <option value="turquoise">Turquoise</option>
            <option value="lavender">Lavender</option>
          </select>
        </div>
        <br></br>
        <Table className="tbl" 
        numOfCols = {this.state.numColumns} 
        numOfRows = {this.state.numRows}
        color = {this.state.color}
        ></Table>
      </div>
    );
  }
}

export default App;
